import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { proposalsAPI } from "../services/api";
// Using Unicode symbols instead of react-icons to avoid TypeScript issues

// Email modal state interface
interface EmailModalState {
  isOpen: boolean;
  email: string;
  loading: boolean;
}

// Define the proposal interface
interface Proposal {
  id: string;
  title: string;
  description?: string;
  clientName?: string;
  clientEmail?: string;
  status: string;
  type: string;
  content: any;
  metadata?: any;
  version: number;
  isPublic: boolean;
  publicUrl?: string;
  createdAt: string;
  updatedAt: string;
  publishedAt?: string;
  userId: string;
  organizationId: string;
  templateId?: string;
}

// Accent color for B2B SaaS
const ACCENT = "#2563eb"; // blue-600
const BG_GRADIENT = "bg-gradient-to-br from-blue-50 via-white to-indigo-50";
const sectionClass = "bg-white rounded-2xl shadow-lg p-8 mb-8 border border-gray-100";
const sectionTitleClass = "flex items-center gap-2 text-xl font-bold mb-4";
const sectionContentClass = "prose max-w-none text-gray-800";

export default function ProposalViewer() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [proposal, setProposal] = React.useState<Proposal | null>(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [emailModal, setEmailModal] = React.useState<EmailModalState>({
    isOpen: false,
    email: "",
    loading: false
  });

  React.useEffect(() => {
    if (!id) return;

    setLoading(true);
    setError(null);

    proposalsAPI.getById(id)
      .then((response) => {
        // Fix: set the actual proposal object, not the wrapper
        setProposal(response.data.data);
        setLoading(false);
      })
      .catch((err) => {
        setError("Failed to load proposal");
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="p-8 text-center text-gray-500">Loading...</div>;
  if (error) return <div className="p-8 text-center text-red-500">{error}</div>;
  if (!proposal) return <div className="p-8 text-center text-gray-500">Proposal not found.</div>;

  console.log('Rendering proposal:', proposal);
  console.log('Proposal content:', proposal.content);
  console.log('Proposal metadata:', proposal.metadata);

  const content = proposal.content || {};
  const metadata = proposal.metadata || {};

  // Handle PDF download
  const handleDownloadPDF = async () => {
    try {
      const response = await proposalsAPI.downloadPDF(id!);
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${proposal.title.replace(/\s+/g, '_')}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Failed to download PDF:', error);
      alert('Failed to download PDF. Please try again.');
    }
  };

  // Handle sending proposal via email
  const handleSendEmail = async () => {
    if (!emailModal.email.trim()) {
      alert('Please enter a valid email address.');
      return;
    }

    setEmailModal(prev => ({ ...prev, loading: true }));
    try {
      await proposalsAPI.sendProposal(id!, { email: emailModal.email });
      alert('Proposal sent successfully!');
      setEmailModal({ isOpen: false, email: "", loading: false });
    } catch (error) {
      console.error('Failed to send proposal:', error);
      alert('Failed to send proposal. Please try again.');
      setEmailModal(prev => ({ ...prev, loading: false }));
    }
  };

  return (
    <div className={`min-h-screen ${BG_GRADIENT} py-10 px-2 sm:px-0 flex justify-center`}>
      <div className="w-full max-w-3xl">
        {/* Header Card */}
        <div className="bg-white rounded-2xl shadow-xl p-8 mb-10 border-l-8" style={{ borderColor: ACCENT }}>
          <div className="flex items-center justify-between mb-2">
            <h1 className="text-3xl font-extrabold text-gray-900 flex items-center gap-3">
              <span style={{ color: ACCENT, fontSize: '32px' }}>📄</span>
              {proposal.title}
            </h1>
            <div className="flex items-center gap-3">
              <button
                onClick={handleDownloadPDF}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200 flex items-center gap-2"
              >
                📄 Download PDF
              </button>
              <button
                onClick={() => setEmailModal(prev => ({ 
                  ...prev, 
                  isOpen: true, 
                  email: proposal.clientEmail || "" 
                }))}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200 flex items-center gap-2"
              >
                ✉️ Send to Client
              </button>
              <button
                className="text-blue-600 hover:underline text-base font-medium"
                onClick={() => navigate(-1)}
              >
                ← Back
              </button>
            </div>
          </div>
          {proposal.description && (
            <div className="text-gray-700 mb-2 text-lg font-medium">{proposal.description}</div>
          )}
          <div className="flex flex-wrap gap-6 text-base text-gray-600 mt-4">
            {proposal.clientName && (
              <div className="flex items-center gap-2"><span style={{ color: ACCENT }}>👤</span> <span className="font-semibold">Client:</span> {proposal.clientName}</div>
            )}
            {proposal.clientEmail && (
              <div className="flex items-center gap-2"><span style={{ color: ACCENT }}>✉️</span> <span className="font-semibold">Email:</span> {proposal.clientEmail}</div>
            )}
            {metadata.industry && (
              <div className="flex items-center gap-2"><span style={{ color: ACCENT }}>🏭</span> <span className="font-semibold">Industry:</span> {metadata.industry}</div>
            )}
            {metadata.companySize && (
              <div className="flex items-center gap-2"><span style={{ color: ACCENT }}>📋</span> <span className="font-semibold">Company Size:</span> {metadata.companySize}</div>
            )}
          </div>
        </div>

        {/* Executive Summary */}
        {content.executiveSummary && (
          <div className={sectionClass}>
            <div className={sectionTitleClass} style={{ color: ACCENT }}>
              💡 Executive Summary
            </div>
            <div className={sectionContentClass} style={{ fontSize: "1.1rem" }}>
              <p className="whitespace-pre-line">{content.executiveSummary}</p>
            </div>
          </div>
        )}

        {/* Problem Statement */}
        {content.problemStatement && (
          <div className={sectionClass}>
            <h2 className={sectionTitleClass}>Problem Statement</h2>
            <div className={sectionContentClass}>
              <p className="text-gray-700 leading-relaxed">
                {typeof content.problemStatement === 'object' 
                  ? JSON.stringify(content.problemStatement, null, 2) 
                  : content.problemStatement}
              </p>
            </div>
          </div>
        )}

        {/* Proposed Solution */}
        {content.solution && (
          <div className={sectionClass}>
            <h2 className={sectionTitleClass}>Proposed Solution</h2>
            <div className={sectionContentClass}>
              <p className="text-gray-700 leading-relaxed">
                {typeof content.solution === 'object' 
                  ? JSON.stringify(content.solution, null, 2) 
                  : content.solution}
              </p>
            </div>
          </div>
        )}

        {/* Approach & Methodology */}
        {content.approach && (
          <div className={sectionClass}>
            <div className={sectionTitleClass} style={{ color: ACCENT }}>
              📋 Approach & Methodology
            </div>
            <div className={sectionContentClass} style={{ fontSize: "1.1rem" }}>
              <p className="whitespace-pre-line">{content.approach}</p>
            </div>
          </div>
        )}

        {/* Budget Details */}
        {content.budgetDetails && (
          <div className={sectionClass}>
            <div className={sectionTitleClass} style={{ color: ACCENT }}>
              💰 Budget Details
            </div>
            <div className={sectionContentClass} style={{ fontSize: "1.1rem" }}>
              {typeof content.budgetDetails === 'object' ? (
                <div className="space-y-4">
                  {content.budgetDetails.total && (
                    <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
                      <h4 className="font-semibold text-blue-900 mb-2">Total Budget</h4>
                      <p className="text-2xl font-bold text-blue-700">{content.budgetDetails.total}</p>
                    </div>
                  )}
                  {content.budgetDetails.breakdown && (
                    <div>
                      <h4 className="font-semibold text-gray-900 mb-3">Cost Breakdown</h4>
                      <div className="space-y-2">
                        {Array.isArray(content.budgetDetails.breakdown) ? (
                          content.budgetDetails.breakdown.map((item: any, index: number) => (
                            <div key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                              <span className="text-gray-700">{item.description || item.name || `Item ${index + 1}`}</span>
                              <span className="font-semibold text-gray-900">{item.cost || item.amount || 'N/A'}</span>
                            </div>
                          ))
                        ) : (
                          <p className="text-gray-700">{JSON.stringify(content.budgetDetails.breakdown, null, 2)}</p>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              ) : (
                <p className="whitespace-pre-line">{content.budgetDetails}</p>
              )}
            </div>
          </div>
        )}

        {/* Project Timeline */}
        {content.timeline && (
          <div className={sectionClass}>
            <div className={sectionTitleClass} style={{ color: ACCENT }}>
              ⏰ Project Timeline
            </div>
            <div className={sectionContentClass} style={{ fontSize: "1.1rem" }}>
              <p className="whitespace-pre-line">{content.timeline}</p>
            </div>
          </div>
        )}

        {/* Investment Summary */}
        {(content.budget || content.pricing) && (
          <div className={sectionClass}>
            <div className={sectionTitleClass} style={{ color: ACCENT }}>
              💰 Investment Summary
            </div>
            <div className={sectionContentClass} style={{ fontSize: "1.1rem" }}>
              {(() => {
                const investmentData = content.budget || content.pricing;
                if (typeof investmentData === 'object') {
                  return (
                    <div className="space-y-4">
                      {investmentData.total && (
                        <div className="bg-green-50 p-4 rounded-lg border border-green-200">
                          <h4 className="font-semibold text-green-900 mb-2">Total Investment</h4>
                          <p className="text-2xl font-bold text-green-700">{investmentData.total}</p>
                        </div>
                      )}
                      {investmentData.breakdown && (
                        <div>
                          <h4 className="font-semibold text-gray-900 mb-3">Pricing Breakdown</h4>
                          <div className="space-y-2">
                            {Array.isArray(investmentData.breakdown) ? (
                              investmentData.breakdown.map((item: any, index: number) => (
                                <div key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                                  <span className="text-gray-700">{item.description || item.name || `Item ${index + 1}`}</span>
                                  <span className="font-semibold text-gray-900">{item.cost || item.amount || 'N/A'}</span>
                                </div>
                              ))
                            ) : (
                              <p className="text-gray-700">{JSON.stringify(investmentData.breakdown, null, 2)}</p>
                            )}
                          </div>
                        </div>
                      )}
                    </div>
                  );
                } else {
                  return <p className="whitespace-pre-line">{investmentData}</p>;
                }
              })()}
            </div>
          </div>
        )}

        {/* Next Steps */}
        {content.nextSteps && (
          <div className={sectionClass}>
            <h2 className={sectionTitleClass}>Next Steps</h2>
            <div className={sectionContentClass}>
              {Array.isArray(content.nextSteps) ? (
                <ul className="list-disc pl-6 text-gray-700">
                  {content.nextSteps.map((step: string, index: number) => (
                    <li key={index} className="mb-2">{step}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-700 leading-relaxed">{content.nextSteps}</p>
              )}
            </div>
          </div>
        )}

        {/* Legacy Support for Old Content Structure */}
        {content.findings && (
          <div className={sectionClass}>
            <h2 className={sectionTitleClass}>Findings</h2>
            <div className={sectionContentClass}>
              <p className="text-gray-700 leading-relaxed">{content.findings}</p>
            </div>
          </div>
        )}

        {content.recommendations && (
          <div className={sectionClass}>
            <h2 className={sectionTitleClass}>Recommendations</h2>
            <div className={sectionContentClass}>
              <p className="text-gray-700 leading-relaxed">{content.recommendations}</p>
            </div>
          </div>
        )}

        {/* Email Modal */}
        {emailModal.isOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4 shadow-2xl">
              <h3 className="text-xl font-bold text-gray-900 mb-4">Send Proposal to Client</h3>
              <div className="mb-4">
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                  Client Email Address
                </label>
                <input
                  type="email"
                  id="email"
                  value={emailModal.email}
                  onChange={(e) => setEmailModal(prev => ({ ...prev, email: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="client@example.com"
                  disabled={emailModal.loading}
                />
              </div>
              <div className="flex gap-3">
                <button
                  onClick={handleSendEmail}
                  disabled={emailModal.loading}
                  className="flex-1 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200"
                >
                  {emailModal.loading ? 'Sending...' : 'Send Proposal'}
                </button>
                <button
                  onClick={() => setEmailModal({ isOpen: false, email: "", loading: false })}
                  disabled={emailModal.loading}
                  className="flex-1 bg-gray-300 hover:bg-gray-400 disabled:bg-gray-200 text-gray-700 px-4 py-2 rounded-lg font-medium transition-colors duration-200"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
