import React, { useState, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import ReactMarkdown from 'react-markdown';
import {
  PaperAirplaneIcon,
  EnvelopeIcon,
  ArrowUpIcon,
  DocumentArrowUpIcon
} from '@heroicons/react/24/outline';
import { generateProposalSuggestions } from '../services/aiService';
import { sendProposalEmail } from '../services/emailService';

interface Suggestion {
  id: string;
  title: string;
  content: string;
  category: string;
}

interface EmailData {
  to: string;
  clientName: string;
  clientEmail: string;
  subject: string;
  message: string;
}

export default function Dashboard() {
  const [inputText, setInputText] = useState('');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [generatedContent, setGeneratedContent] = useState('');
  const [showContent, setShowContent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [suggestions, setSuggestions] = useState<Suggestion[]>([]);
  const [selectedSuggestions, setSelectedSuggestions] = useState<string[]>([]);
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [emailData, setEmailData] = useState<EmailData>({
    to: '',
    clientName: '',
    clientEmail: '',
    subject: '',
    message: ''
  });

  useEffect(() => {
    loadSuggestions();
  }, []);

  const loadSuggestions = async () => {
    try {
      const newSuggestions = await generateProposalSuggestions();
      setSuggestions(newSuggestions);
    } catch (error) {
      console.error('Failed to load suggestions:', error);
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      toast.success('PDF file selected successfully!');
    } else if (file) {
      toast.error('Please select a PDF file');
    }
  };

  const handleGenerateWithAI = async () => {
    if (!selectedFile && !inputText.trim()) {
      toast.error('Please enter some text or upload a PDF file');
      return;
    }

    setLoading(true);
    try {
      // Simulate AI generation
      await new Promise(resolve => setTimeout(resolve, 2000));
      setGeneratedContent('# Generated Proposal\n\nThis is a sample generated proposal content. You can customize this based on your specific requirements.\n\n## Key Features\n- Feature 1\n- Feature 2\n- Feature 3\n\n## Benefits\n- Benefit 1\n- Benefit 2\n- Benefit 3');
      setShowContent(true);
      toast.success('Proposal generated successfully!');
    } catch (error) {
      toast.error('Failed to generate proposal');
    } finally {
      setLoading(false);
    }
  };

  const handleSuggestionClick = (suggestion: Suggestion) => {
    if (selectedSuggestions.includes(suggestion.id)) {
      setSelectedSuggestions(selectedSuggestions.filter(id => id !== suggestion.id));
    } else {
      setSelectedSuggestions([...selectedSuggestions, suggestion.id]);
    }
  };

  const handleSendEmail = async () => {
    try {
      await sendProposalEmail({
        to: emailData.clientEmail,
        clientName: emailData.clientName,
        clientEmail: emailData.clientEmail,
        subject: emailData.subject,
        message: emailData.message
      });
      toast.success('Email sent successfully!');
      setShowEmailModal(false);
    } catch (error) {
      toast.error('Failed to send email');
    }
  };

  const handleClear = () => {
    setInputText('');
    setSelectedFile(null);
    setGeneratedContent('');
    setShowContent(false);
    setSelectedSuggestions([]);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            What can I help with?
          </h1>
        </div>

        {/* Input Area */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <div className="relative">
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              placeholder="Ask ProposalAI or type / to see prompts..."
              rows={4}
            />

            {/* PDF Upload inside textbox */}
            <div className="absolute bottom-3 right-3">
              <label htmlFor="pdf-upload" className="cursor-pointer">
                <div className="p-2 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors">
                  <DocumentArrowUpIcon className="h-5 w-5 text-gray-600" />
                </div>
              </label>
              <input
                id="pdf-upload"
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                className="hidden"
              />
            </div>

            {selectedFile && (
              <div className="mt-2 text-sm text-green-600 flex items-center">
                <DocumentArrowUpIcon className="h-4 w-4 mr-1" />
                {selectedFile.name}
              </div>
            )}
          </div>

          {/* Suggestion Bubbles */}
          {suggestions.length > 0 && (
            <div className="mt-4">
              <div className="flex flex-wrap gap-2">
                {suggestions.slice(0, 6).map((suggestion) => (
                  <button
                    key={suggestion.id}
                    onClick={() => handleSuggestionClick(suggestion)}
                    className={`px-3 py-1.5 text-sm rounded-full border-2 transition-all duration-200 ${
                      selectedSuggestions.includes(suggestion.id)
                        ? 'bg-blue-100 text-blue-700 border-blue-300'
                        : 'bg-gray-50 text-gray-700 border-gray-200 hover:bg-gray-100'
                    }`}
                  >
                    {suggestion.title}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Action Row */}
          <div className="flex items-center justify-end mt-4">
            <button
              onClick={handleGenerateWithAI}
              disabled={(!selectedFile && !inputText.trim()) || loading}
              className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2"
            >
              <ArrowUpIcon className="h-4 w-4" />
              <span>{loading ? 'Generating...' : 'Generate'}</span>
            </button>
          </div>
        </div>

        {/* Generated Content Display */}
        {showContent && (
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Generated Content</h2>
            <div className="prose prose-sm max-w-none text-gray-600 mb-6">
              <ReactMarkdown>{generatedContent}</ReactMarkdown>
            </div>
            <div className="flex gap-3">
              <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                Save as Draft
              </button>
              <button 
                onClick={() => setShowEmailModal(true)}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors flex items-center space-x-2"
              >
                <EnvelopeIcon className="h-4 w-4" />
                <span>Send Email</span>
              </button>
              <button
                onClick={handleClear}
                className="px-4 py-2 bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition-colors"
              >
                Clear
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Email Modal */}
      {showEmailModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Send Proposal Email</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Client Name
                </label>
                <input
                  type="text"
                  value={emailData.clientName}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientName: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter client name"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Client Email *
                </label>
                <input
                  type="email"
                  value={emailData.clientEmail}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientEmail: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter client email"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Subject *
                </label>
                <input
                  type="text"
                  value={emailData.subject}
                  onChange={(e) => setEmailData(prev => ({ ...prev, subject: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter email subject"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Message *
                </label>
                <textarea
                  value={emailData.message}
                  onChange={(e) => setEmailData(prev => ({ ...prev, message: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 min-h-[100px]"
                  placeholder="Enter your message"
                  required
                />
              </div>
            </div>

            <div className="flex gap-3 mt-6">
              <button
                onClick={handleSendEmail}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex-1"
              >
                <PaperAirplaneIcon className="h-4 w-4 inline mr-1" />
                Send Email
              </button>
              <button
                onClick={() => setShowEmailModal(false)}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Loading Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 flex items-center space-x-3">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
            <span className="text-gray-900">Generating proposal...</span>
          </div>
        </div>
      )}
    </div>
  );
}
