import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  BellIcon, 
  ChatBubbleLeftIcon, 
  EyeIcon, 
  CheckCircleIcon, 
  XCircleIcon,
  UserIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import { notificationAPI } from '../services/api';
import toast from 'react-hot-toast';
import { useAuth } from '../hooks/useAuth';

interface Notification {
  id: string;
  type: 'COMMENT' | 'PROPOSAL_OPENED' | 'PROPOSAL_APPROVED' | 'PROPOSAL_REJECTED' | 'CLIENT_REPLY' | 'ACCESS_REQUEST';
  title: string;
  message: string;
  isRead: boolean;
  createdAt: string;
  proposalId?: string;
  proposalTitle?: string;
  clientName?: string;
}

export default function NotificationBell() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {
    if (user) {
      fetchNotifications();
      fetchUnreadCount();

      // Poll for new notifications every 30 seconds
      const interval = setInterval(() => {
        fetchUnreadCount();
      }, 30000);

      return () => clearInterval(interval);
    }
  }, [user]);

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      setError(false);
      const response = await notificationAPI.getAll();
      if (response.data.success) {
        setNotifications(response.data.data);
      }
    } catch (error) {
      console.error('Failed to fetch notifications:', error);
      setError(true);
    } finally {
      setLoading(false);
    }
  };

  const fetchUnreadCount = async () => {
    try {
      const response = await notificationAPI.getUnreadCount();
      if (response.data.success) {
        setUnreadCount(response.data.data.unreadCount);
      }
    } catch (error) {
      console.error('Failed to fetch unread count:', error);
      // Don't set error here as we still want to show the bell
    }
  };

  const handleNotificationClick = async (notification: Notification) => {
    try {
      // Mark as read first
      await markAsRead(notification.id);

      // Close the dropdown
      setIsOpen(false);

      // Navigate based on notification type
      switch (notification.type) {
        case 'COMMENT':
        case 'CLIENT_REPLY':
          if (notification.proposalId) {
            // Navigate to proposal with comments tab
            navigate(`/proposals/${notification.proposalId}/view?tab=comments`);
            toast.success('Navigating to comments...');
          } else {
            navigate('/sent-proposals');
            toast.info('Proposal not found, showing sent proposals');
          }
          break;

        case 'PROPOSAL_OPENED':
        case 'PROPOSAL_APPROVED':
        case 'PROPOSAL_REJECTED':
          if (notification.proposalId) {
            // Navigate to proposal view
            navigate(`/proposals/${notification.proposalId}/view`);
            toast.success('Navigating to proposal...');
          } else {
            navigate('/sent-proposals');
            toast.info('Proposal not found, showing sent proposals');
          }
          break;

        case 'ACCESS_REQUEST':
          if (notification.proposalId) {
            // Navigate to proposal with activity tab
            navigate(`/proposals/${notification.proposalId}/view?tab=activity`);
            toast.success('Navigating to proposal activity...');
          } else {
            navigate('/sent-proposals');
            toast.info('Proposal not found, showing sent proposals');
          }
          break;

        default:
          // Fallback to sent proposals
          navigate('/sent-proposals');
          toast.info('Navigating to sent proposals');
          break;
      }
    } catch (error) {
      console.error('Failed to handle notification click:', error);
      toast.error('Failed to navigate to notification');
    }
  };

  const markAsRead = async (notificationId: string) => {
    try {
      await notificationAPI.markAsRead(notificationId);
      setNotifications(prev => 
        prev.map(notif => 
          notif.id === notificationId ? { ...notif, isRead: true } : notif
        )
      );
      setUnreadCount(prev => Math.max(0, prev - 1));
    } catch (error) {
      console.error('Failed to mark notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await notificationAPI.markAllAsRead();
      setNotifications(prev => prev.map(notif => ({ ...notif, isRead: true })));
      setUnreadCount(0);
      toast.success('All notifications marked as read');
    } catch (error) {
      console.error('Failed to mark all notifications as read:', error);
      toast.error('Failed to mark notifications as read');
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'COMMENT':
        return <ChatBubbleLeftIcon className="h-5 w-5 text-blue-500" />;
      case 'PROPOSAL_OPENED':
        return <EyeIcon className="h-5 w-5 text-green-500" />;
      case 'PROPOSAL_APPROVED':
        return <CheckCircleIcon className="h-5 w-5 text-green-600" />;
      case 'PROPOSAL_REJECTED':
        return <XCircleIcon className="h-5 w-5 text-red-500" />;
      case 'CLIENT_REPLY':
        return <UserIcon className="h-5 w-5 text-purple-500" />;
      case 'ACCESS_REQUEST':
        return <UserIcon className="h-5 w-5 text-orange-500" />;
      default:
        return <BellIcon className="h-5 w-5 text-gray-500" />;
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'COMMENT':
        return 'bg-blue-50 border-blue-200';
      case 'PROPOSAL_OPENED':
        return 'bg-green-50 border-green-200';
      case 'PROPOSAL_APPROVED':
        return 'bg-green-50 border-green-200';
      case 'PROPOSAL_REJECTED':
        return 'bg-red-50 border-red-200';
      case 'CLIENT_REPLY':
        return 'bg-purple-50 border-purple-200';
      case 'ACCESS_REQUEST':
        return 'bg-orange-50 border-orange-200';
      default:
        return 'bg-gray-50 border-gray-200';
    }
  };

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return `${Math.floor(diffInMinutes / 1440)}d ago`;
  };

  return (
    <div className="relative">
      {/* Notification Bell Button - Always show, even if API fails */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 text-white/80 hover:text-white hover:bg-white/10 rounded-lg transition-colors"
        title={user ? "Notifications" : "Login to see notifications"}
      >
        <BellIcon className="h-6 w-6" />
        {user && unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-medium">
            {unreadCount > 99 ? '99+' : unreadCount}
          </span>
        )}
        {/* Show a subtle indicator if there's an error */}
        {error && (
          <span className="absolute -bottom-1 -right-1 bg-yellow-400 text-white text-xs rounded-full h-3 w-3 flex items-center justify-center">
            !
          </span>
        )}
      </button>

      {/* Notification Dropdown */}
      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg border border-gray-200 z-50 max-h-96 overflow-hidden">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">Notifications</h3>
            {unreadCount > 0 && (
              <button
                onClick={markAllAsRead}
                className="text-sm text-blue-600 hover:text-blue-700 font-medium"
              >
                Mark all read
              </button>
            )}
          </div>

          {/* Notifications List */}
          <div className="max-h-80 overflow-y-auto">
            {loading ? (
              <div className="p-4 text-center text-gray-500">
                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mx-auto"></div>
                <p className="mt-2">Loading notifications...</p>
              </div>
            ) : notifications.length === 0 ? (
              <div className="p-4 text-center text-gray-500">
                <BellIcon className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                <p>
                  {!user ? 'Please login to see notifications' : 
                   error ? 'Unable to load notifications' : 'No notifications yet'}
                </p>
                {error && (
                  <p className="text-xs text-gray-400 mt-1">Check console for details</p>
                )}
              </div>
            ) : (
              <div className="divide-y divide-gray-100">
                {notifications.map((notification) => (
                  <div
                    key={notification.id}
                    className={`p-4 hover:bg-blue-50 transition-colors cursor-pointer border-l-4 ${
                      !notification.isRead ? 'bg-blue-50 border-l-blue-500' : 'border-l-transparent'
                    } hover:border-l-blue-400`}
                    onClick={() => handleNotificationClick(notification)}
                  >
                    <div className="flex items-start gap-3">
                      <div className="flex-shrink-0 mt-1">
                        {getNotificationIcon(notification.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-1">
                          <p className={`text-sm font-medium ${
                            !notification.isRead ? 'text-gray-900' : 'text-gray-700'
                          }`}>
                            {notification.title}
                          </p>
                          {!notification.isRead && (
                            <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                          )}
                        </div>
                        <p className="text-sm text-gray-600 mb-2">
                          {notification.message}
                        </p>
                        <div className="flex items-center gap-2 text-xs text-gray-400">
                          <ClockIcon className="h-3 w-3" />
                          {formatTimeAgo(notification.createdAt)}
                          <span className="text-blue-500 font-medium">Click to view â†’</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          {notifications.length > 0 && (
            <div className="p-3 border-t border-gray-200 bg-gray-50">
              <button
                onClick={() => {
                  setIsOpen(false);
                  navigate('/sent-proposals');
                }}
                className="w-full text-sm text-blue-600 hover:text-blue-700 font-medium"
              >
                View all proposals
              </button>
            </div>
          )}
        </div>
      )}

      {/* Backdrop */}
      {isOpen && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
}
