            const aiContent = aiResponse.choices[0]?.message?.content || '';
            const lines = aiContent.split('\n').filter((line: string) => line.trim());
            const aiSuggestions = lines
              .map((line: string) => line.replace(/^\d+\.\s*/, '').trim())
