import React, { useState } from 'react';
import { toast } from 'react-hot-toast';

const metrics = [
  {
    label: 'Total Items',
    value: 5,
    icon: (
      <svg className="w-7 h-7 text-primary-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="7" width="18" height="13" rx="2" /><path d="M16 3v4M8 3v4" /></svg>
    ),
    bg: 'bg-blue-50',
  },
  {
    label: 'Case Studies',
    value: 1,
    icon: (
      <svg className="w-7 h-7 text-purple-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="4" y="4" width="16" height="16" rx="2" /><path d="M8 8h8v8H8z" /></svg>
    ),
    bg: 'bg-purple-50',
  },
  {
    label: 'Avg. Effectiveness',
    value: '8.6/10',
    icon: (
      <svg className="w-7 h-7 text-yellow-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><polygon points="12 2 15 8.5 22 9.3 17 14.1 18.2 21.1 12 17.8 5.8 21.1 7 14.1 2 9.3 9 8.5 12 2" /></svg>
    ),
    bg: 'bg-yellow-50',
  },
  {
    label: 'Total Usage',
    value: 59,
    change: '+23% this month',
    icon: (
      <svg className="w-7 h-7 text-green-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M3 17l6-6 4 4 8-8" /><path d="M21 21H3V3" /></svg>
    ),
    bg: 'bg-green-50',
  },
];

const filters = [
  'All Types',
  'testimonial',
  'case study',
  'service offering',
  'methodology',
  'pricing model',
];

const knowledge = [
  {
    title: 'Client Testimonial - Manufacturing',
    type: 'testimonial',
    typeColor: 'bg-yellow-100 text-yellow-800',
    content: `InnovateTech transformed our operations with their IoT solution. We've seen 30% improvement in equipment efficiency and $2M annual savings...`,
    tags: ['testimonial', 'IoT', 'manufacturing', '+1 more'],
    uses: 18,
    rating: '10/10',
  },
  {
    title: 'Healthcare Digital Transformation',
    type: 'case study',
    typeColor: 'bg-purple-100 text-purple-800',
    content: `Successfully transformed a 500-bed hospital's legacy systems, resulting in 40% reduction in patient wait times and 25% cost savings....`,
    tags: ['healthcare', 'digital transformation', 'EHR', '+1 more'],
    uses: 12,
    rating: '9/10',
  },
  {
    title: 'Cloud Migration Service Offering',
    type: 'service offering',
    typeColor: 'bg-blue-100 text-blue-800',
    content: `End-to-end cloud migration services including assessment, architecture design, migration execution, and optimization. We support AWS,...`,
    tags: ['cloud', 'migration', 'AWS', '+2 more'],
    uses: 15,
    rating: '9/10',
  },
];

export default function KnowledgeBase() {
  const [selectedFilter, setSelectedFilter] = useState('All Types');
  const [search, setSearch] = useState('');

  return (
    <div className="pb-8">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
        <div>
          <h2 className="text-3xl font-bold text-gray-900 mb-1 flex items-center gap-3">
            <svg className="w-9 h-9 text-primary-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="7" width="18" height="13" rx="2" /><path d="M16 3v4M8 3v4" /></svg>
            Knowledge Base
          </h2>
          <p className="text-gray-500 text-lg">Manage your company's content library for AI-powered proposals</p>
        </div>
        <button 
          className="btn btn-primary px-6 py-2 text-base font-semibold mt-4 md:mt-0"
          onClick={() => toast.success('Add Knowledge feature coming soon!')}
        >
          + Add Knowledge
        </button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        {metrics.map((metric, i) => (
          <div key={i} className={`rounded-xl p-5 flex flex-col gap-2 shadow-soft ${metric.bg}`}>
            <div className="flex items-center gap-2">
              <div className="rounded-lg p-2 bg-white shadow-sm">
                {metric.icon}
              </div>
              <span className="text-xs font-medium text-gray-500">{metric.label}</span>
            </div>
            <div className="text-2xl font-bold text-gray-900">{metric.value}</div>
            {metric.change && <div className="text-xs text-green-600">{metric.change}</div>}
          </div>
        ))}
      </div>
      <div className="flex flex-col md:flex-row md:items-center gap-4 mb-8">
        <div className="flex-1 flex items-center bg-white rounded-lg shadow-soft px-4 py-2">
          <svg className="w-5 h-5 text-gray-400 mr-2" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="11" cy="11" r="8" /><path d="M21 21l-4.35-4.35" /></svg>
          <input
            className="w-full bg-transparent outline-none text-gray-700 placeholder-gray-400"
            placeholder="Search knowledge base..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
        <div className="flex flex-wrap gap-2 mt-2 md:mt-0">
          {filters.map(filter => (
            <button
              key={filter}
              className={`px-4 py-1.5 rounded-lg font-medium text-sm border transition ${selectedFilter === filter ? 'bg-primary-600 text-white border-primary-600' : 'bg-white text-gray-700 border-gray-200 hover:bg-gray-50'}`}
              onClick={() => setSelectedFilter(filter)}
            >
              {filter}
            </button>
          ))}
        </div>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
        {knowledge.map((item, i) => (
          <div key={i} className="bg-white rounded-2xl shadow-soft p-6 flex flex-col gap-3">
            <div className="flex items-center gap-2 mb-2">
              <span className="rounded-full p-1.5 bg-gray-50 border border-gray-200">
                {item.type === 'testimonial' && <svg className="w-5 h-5 text-yellow-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><polygon points="12 2 15 8.5 22 9.3 17 14.1 18.2 21.1 12 17.8 5.8 21.1 7 14.1 2 9.3 9 8.5 12 2" /></svg>}
                {item.type === 'case study' && <svg className="w-5 h-5 text-purple-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="4" y="4" width="16" height="16" rx="2" /><path d="M8 8h8v8H8z" /></svg>}
                {item.type === 'service offering' && <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="7" width="18" height="13" rx="2" /><path d="M16 3v4M8 3v4" /></svg>}
              </span>
              <span className="font-semibold text-gray-900 truncate mr-2">{item.title}</span>
              <span className={`badge ${item.typeColor} capitalize ml-auto`}>{item.type}</span>
            </div>
            <div className="text-gray-700 text-sm mb-2 truncate-2-lines">{item.content}</div>
            <div className="flex flex-wrap gap-2 mb-2">
              {item.tags.map((tag, j) => (
                <span key={j} className="bg-gray-100 text-gray-700 rounded-full px-3 py-0.5 text-xs font-medium">{tag}</span>
              ))}
            </div>
            <div className="flex items-center justify-between text-xs text-gray-500 mt-auto pt-2 border-t border-gray-100">
              <span>{item.uses} uses</span>
              <span>{item.rating}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
