import React, { useState, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import ReactMarkdown from 'react-markdown';
import {
  PlusIcon,
  PaperAirplaneIcon,
  EnvelopeIcon
} from '@heroicons/react/24/outline';
import { generateProposalSuggestions } from '../services/aiService';
import { sendProposalEmail } from '../services/emailService';

interface Suggestion {
  id: string;
  title: string;
  content: string;
  category: string;
}

export default function Dashboard() {
  const [showContent, setShowContent] = useState(false);
  const [generatedContent, setGeneratedContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [suggestions, setSuggestions] = useState<Suggestion[]>([]);
  const [selectedSuggestions, setSelectedSuggestions] = useState<string[]>([]);
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [emailData, setEmailData] = useState({
    to: '',
    subject: '',
    message: '',
    clientName: '',
    clientEmail: ''
  });
  const [inputText, setInputText] = useState('');

  // Load suggestions on component mount
  useEffect(() => {
    loadSuggestions();
  }, []);

  const loadSuggestions = async () => {
    try {
      const newSuggestions = await generateProposalSuggestions();
      setSuggestions(newSuggestions);
    } catch (error) {
      console.error('Error loading suggestions:', error);
      toast.error('Failed to load suggestions');
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleGenerateWithAI = async () => {
    if (!selectedFile && !inputText.trim()) {
      toast.error('Please select a PDF file or enter some text first');
      return;
    }

    setLoading(true);
    try {
      let content = '';

      if (selectedFile) {
        const formData = new FormData();
        formData.append('pdf', selectedFile);

        const response = await fetch('/api/proposals/extract-pdf', {
          method: 'POST',
          body: formData,
        });

        if (!response.ok) {
          throw new Error('Failed to process PDF');
        }

        const data = await response.json();
        content = data.content;
      } else if (inputText.trim()) {
        content = inputText;
      }

      setGeneratedContent(content);
      setShowContent(true);
      toast.success('Content generated successfully!');
    } catch (error) {
      console.error('Error generating content:', error);
      toast.error('Failed to generate content. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setSelectedFile(null);
    setGeneratedContent('');
    setShowContent(false);
    setSelectedSuggestions([]);
    setInputText('');
    const fileInput = document.getElementById('pdf-upload') as HTMLInputElement;
    if (fileInput) {
      fileInput.value = '';
    }
  };

  const handleSuggestionClick = (suggestion: Suggestion) => {
    setSelectedSuggestions(prev => {
      if (prev.includes(suggestion.id)) {
        return prev.filter(id => id !== suggestion.id);
      } else {
        return [...prev, suggestion.id];
      }
    });
  };

  const handleSendEmail = async () => {
    if (!emailData.clientEmail || !emailData.subject || !emailData.message) {
      toast.error('Please fill in all required fields');
      return;
    }

    try {
      await sendProposalEmail({
        to: emailData.clientEmail,
        subject: emailData.subject,
        message: emailData.message,
        clientName: emailData.clientName,
        clientEmail: emailData.clientEmail
      });

      toast.success('Email sent successfully!');
      setShowEmailModal(false);
      setEmailData({ to: '', subject: '', message: '', clientName: '', clientEmail: '' });
    } catch (error) {
      console.error('Error sending email:', error);
      toast.error('Failed to send email');
    }
  };

  return (
    <div className="px-4 sm:px-6 lg:px-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Dashboard</h1>
        <p className="text-gray-600">Welcome to your proposal management dashboard</p>
      </div>

      {/* Main Content Area */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Left Column - Input and Generate */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">Generate Proposal</h2>

            {/* Text Input */}
            <div className="mb-4">
              <label htmlFor="proposal-text" className="block text-sm font-medium text-gray-700 mb-2">
                Enter your proposal text or requirements
              </label>
              <textarea
                id="proposal-text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent min-h-[120px]"
                placeholder="Describe your proposal requirements, project details, or paste existing content..."
              />
            </div>

            {/* File Upload */}
            <div className="mb-6">
              <label htmlFor="pdf-upload" className="block text-sm font-medium text-gray-700 mb-2">
                Or upload a PDF file
              </label>
              <input
                id="pdf-upload"
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Action Buttons */}
            <div className="flex gap-3">
              <button
                onClick={handleGenerateWithAI}
                disabled={(!selectedFile && !inputText.trim()) || loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Generating...' : 'Generate Proposal'}
              </button>
              <button
                onClick={handleClear}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
              >
                Clear
              </button>
            </div>
          </div>

          {/* Generated Content Display */}
          {showContent && (
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mt-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Generated Content</h2>
              <div className="prose prose-sm max-w-none text-gray-600 mb-4">
                <ReactMarkdown>{generatedContent}</ReactMarkdown>
              </div>
              <div className="flex gap-3">
                <button className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                  Save as Draft
                </button>
                <button 
                  onClick={() => setShowEmailModal(true)}
                  className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
                >
                  <EnvelopeIcon className="h-4 w-4 inline mr-1" />
                  Send Email
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Right Column - Suggestions */}
        <div className="lg:col-span-1">
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold text-gray-900">AI Suggestions</h2>
              <button
                onClick={loadSuggestions}
                className="px-3 py-1 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 text-sm"
              >
                <PlusIcon className="h-4 w-4 inline mr-1" />
                Refresh
              </button>
            </div>

            <div className="space-y-3">
              {suggestions.map((suggestion) => (
                <div
                  key={suggestion.id}
                  className={`p-3 rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                    selectedSuggestions.includes(suggestion.id)
                      ? 'border-blue-500 bg-blue-50'
                      : 'border-gray-200 hover:border-gray-300'
                  }`}
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  <h3 className="font-medium text-gray-900 mb-1">{suggestion.title}</h3>
                  <p className="text-sm text-gray-600 line-clamp-2">{suggestion.content}</p>
                  <span className="inline-block mt-2 px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded">
                    {suggestion.category}
                  </span>
                </div>
              ))}
            </div>

            {selectedSuggestions.length > 0 && (
              <div className="mt-4 p-3 bg-gray-50 rounded-lg">
                <h3 className="font-medium text-gray-900 mb-1">Selected ({selectedSuggestions.length})</h3>
                <p className="text-sm text-gray-600">
                  These suggestions will be included in your generation.
                </p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Email Modal */}
      {showEmailModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Send Proposal Email</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Client Name
                </label>
                <input
                  type="text"
                  value={emailData.clientName}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientName: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter client name"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Client Email *
                </label>
                <input
                  type="email"
                  value={emailData.clientEmail}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientEmail: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter client email"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Subject *
                </label>
                <input
                  type="text"
                  value={emailData.subject}
                  onChange={(e) => setEmailData(prev => ({ ...prev, subject: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter email subject"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Message *
                </label>
                <textarea
                  value={emailData.message}
                  onChange={(e) => setEmailData(prev => ({ ...prev, message: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 min-h-[100px]"
                  placeholder="Enter your message"
                  required
                />
              </div>
            </div>

            <div className="flex gap-3 mt-6">
              <button
                onClick={handleSendEmail}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex-1"
              >
                <PaperAirplaneIcon className="h-4 w-4 inline mr-1" />
                Send Email
              </button>
              <button
                onClick={() => setShowEmailModal(false)}
                className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Loading Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 flex items-center space-x-3">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
            <span className="text-gray-900">Generating proposal...</span>
          </div>
        </div>
      )}
    </div>
  );
}
