import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { proposalsAPI, organizationsAPI } from '../services/api';
import { useTheme } from '../contexts/ThemeContext';

interface Proposal {
  id: string;
  title: string;
  clientName: string;
  status: string;
  content: any;
  createdAt: string;
  updatedAt: string;
}

// Removed unused metrics variable - now using realMetrics calculated from actual data

// Removed unused proposals variable - now using real proposals from API

export default function Dashboard() {
  const navigate = useNavigate();
  const { updateTheme } = useTheme();
  const [proposals, setProposals] = useState<Proposal[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  // Add state for Brand Settings modal
  const [showBrandModal, setShowBrandModal] = useState(false);
  const [brandSettings, setBrandSettings] = useState({
    name: '',
    description: '',
    logo: '',
    website: '',
    industry: '',
    size: '',
    primaryColor: '#3B82F6',
    secondaryColor: '#1F2937',
    fontFamily: 'Inter',
    brandVoice: 'Professional and friendly',
    brandGuidelines: '',
    valueProps: [] as string[]
  });
  const [savingBrand, setSavingBrand] = useState(false);

  useEffect(() => {
    fetchProposals();
    fetchBrandSettings();
  }, []);

  const fetchProposals = async () => {
    try {
      setLoading(true);
      const response = await proposalsAPI.getAll({ limit: 5 });
      setProposals(response.data.data);
    } catch (err) {
      console.error('Failed to fetch proposals:', err);
      setError('Failed to load proposals');
    } finally {
      setLoading(false);
    }
  };

  const fetchBrandSettings = async () => {
    try {
      const response = await organizationsAPI.getBrandSettings();
      setBrandSettings(response.data);

      // Apply brand colors to theme
      if (response.data.primaryColor && response.data.secondaryColor && response.data.fontFamily) {
        updateTheme({
          primaryColor: response.data.primaryColor,
          secondaryColor: response.data.secondaryColor,
          fontFamily: response.data.fontFamily
        });
      }
    } catch (err) {
      console.error('Failed to fetch brand settings:', err);
    }
  };

  const saveBrandSettings = async (settings: any) => {
    try {
      setSavingBrand(true);
      const response = await organizationsAPI.updateBrandSettings(settings);
      setBrandSettings(response.data);
      setShowBrandModal(false);
    } catch (err) {
      console.error('Failed to save brand settings:', err);
      alert('Failed to save brand settings');
    } finally {
      setSavingBrand(false);
    }
  };

  // Calculate metrics from real data
  const totalProposals = proposals.length;
  const wonProposals = proposals.filter(p => p.status === 'WON').length;
  const winRate = totalProposals > 0 ? ((wonProposals / totalProposals) * 100).toFixed(1) : '0';
  const pipelineValue = proposals
    .filter(p => ['DRAFT', 'IN_REVIEW', 'SENT'].includes(p.status))
    .reduce((sum, p) => {
      const budget = p.content?.budget || '$0';
      const value = parseInt(budget.replace(/[^0-9]/g, ''));
      return sum + value;
    }, 0);

  const realMetrics = [
    {
      label: 'Total Proposals',
      value: totalProposals,
      change: '+12% this month',
      icon: (
        <svg className="w-6 h-6 text-primary-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M9 17v-2a2 2 0 012-2h2a2 2 0 012 2v2m-6 4h6a2 2 0 002-2V7a2 2 0 00-2-2H7a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg>
      ),
      bg: 'bg-blue-50',
      text: 'text-primary-700',
    },
    {
      label: 'Win Rate',
      value: `${winRate}%`,
      change: '+5.2% vs last month',
      icon: (
        <svg className="w-6 h-6 text-green-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /></svg>
      ),
      bg: 'bg-green-50',
      text: 'text-green-700',
    },
    {
      label: 'Pipeline Value',
      value: `$${(pipelineValue / 1000).toFixed(0)}K`,
      change: '+18% this quarter',
      icon: (
        <svg className="w-6 h-6 text-purple-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8c-1.657 0-3 1.343-3 3s1.343 3 3 3 3-1.343 3-3-1.343-3-3-3zm0 0V4m0 7v7" /></svg>
      ),
      bg: 'bg-purple-50',
      text: 'text-purple-700',
    },
    {
      label: 'Avg. Time Saved',
      value: '3.2h',
      change: '75% faster than manual',
      icon: (
        <svg className="w-6 h-6 text-orange-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /></svg>
      ),
      bg: 'bg-orange-50',
      text: 'text-orange-700',
    },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'WON': return 'bg-green-100 text-green-800';
      case 'LOST': return 'bg-red-100 text-red-800';
      case 'IN_REVIEW': return 'bg-yellow-100 text-yellow-800';
      case 'SENT': return 'bg-blue-100 text-blue-800';
      case 'DRAFT': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  return (
    <div className="pb-8">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
        <div>
          <h2 className="text-3xl font-bold text-gray-900 mb-1">Welcome back</h2>
          <p className="text-gray-500 text-lg">Your AI-powered proposal workspace is ready to create winning proposals</p>
        </div>
        <div className="flex gap-2 mt-4 md:mt-0">
          <button className="btn btn-secondary" onClick={() => navigate('/knowledge-base')}>Knowledge Base</button>
          <button className="btn btn-primary" onClick={() => navigate('/proposals/new')}>Create Proposal</button>
        </div>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        {realMetrics.map((metric, i) => (
          <div key={i} className={`rounded-xl p-5 flex flex-col gap-2 shadow-soft ${metric.bg}`}>
            <div className="flex items-center gap-2">
              <div className="rounded-lg p-2 bg-white shadow-sm">
                {metric.icon}
              </div>
              <span className="text-xs font-medium text-gray-500">{metric.label}</span>
            </div>
            <div className={`text-2xl font-bold ${metric.text}`}>{metric.value}</div>
            <div className="text-xs text-gray-400">{metric.change}</div>
          </div>
        ))}
      </div>
      <div className="flex flex-col lg:flex-row gap-8">
        <div className="flex-1">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-xl font-semibold text-gray-900">Recent Proposals</h3>
            <button className="text-primary-600 font-medium hover:underline" onClick={() => navigate('/proposals')}>View All</button>
          </div>
          <div className="bg-white rounded-xl shadow-soft divide-y divide-gray-100">
            {loading ? (
              <div className="px-6 py-4 text-center text-gray-500">Loading proposals...</div>
            ) : error ? (
              <div className="px-6 py-4 text-center text-red-500">{error}</div>
            ) : proposals.length === 0 ? (
              <div className="px-6 py-4 text-center text-gray-500">No proposals found</div>
            ) : (
              proposals.map((proposal, i) => (
                <div key={proposal.id} className="flex items-center justify-between px-6 py-4">
                  <div>
                    <div className="font-medium text-gray-900 flex items-center gap-2">
                      <svg className="w-5 h-5 text-primary-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M9 17v-2a2 2 0 012-2h2a2 2 0 012 2v2m-6 4h6a2 2 0 002-2V7a2 2 0 00-2-2H7a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg>
                      {proposal.title}
                    </div>
                    <div className="text-xs text-gray-500 flex gap-2 mt-1">
                      <span>{proposal.clientName}</span>
                      <span>•</span>
                      <span>{formatDate(proposal.createdAt)}</span>
                      <span>•</span>
                      <span>{proposal.content?.budget || 'N/A'}</span>
                    </div>
                  </div>
                  <span className={`badge ${getStatusColor(proposal.status)} capitalize`}>{proposal.status.toLowerCase()}</span>
                </div>
              ))
            )}
          </div>
        </div>
        <div className="w-full lg:w-80 flex-shrink-0">
          <div className="bg-white rounded-xl shadow-soft p-6 mb-6">
            <h4 className="text-lg font-semibold mb-4">Quick Actions</h4>
            <button className="btn btn-primary w-full mb-3" onClick={() => navigate('/proposals/new')}>+ Create New Proposal</button>
            <button className="btn btn-secondary w-full mb-3" onClick={() => navigate('/knowledge-base')}>Manage Knowledge Base</button>
            <button className="btn btn-secondary w-full" onClick={() => setShowBrandModal(true)}>Brand Settings</button>
          </div>
          <div className="bg-white rounded-xl shadow-soft p-6">
            <h4 className="text-lg font-semibold mb-4">Top Knowledge Items</h4>
            <ul className="space-y-2">
              <li className="flex items-center justify-between text-sm text-gray-700">
                <span>Client Testimonial - Manufacturing</span>
                <span className="text-xs text-gray-400">18 uses</span>
              </li>
              <li className="flex items-center justify-between text-sm text-gray-700">
                <span>Cloud Migration Service Offering</span>
                <span className="text-xs text-gray-400">15 uses</span>
              </li>
            </ul>
          </div>
        </div>
      </div>
      {/* Brand Settings Modal */}
      {showBrandModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-start justify-center p-4 z-50 overflow-y-auto">
          <div className="bg-white rounded-2xl max-w-2xl w-full p-8 shadow-2xl relative my-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-2">
              <span className="inline-block w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center text-primary-600 text-xl font-bold">🏢</span>
              Brand Settings
            </h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              saveBrandSettings(brandSettings);
            }} className="space-y-6">
              {/* Company Info */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Company Information</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Company Name</label>
                    <input 
                      type="text" 
                      className="input w-full" 
                      placeholder="e.g. TechCorp Solutions"
                      value={brandSettings.name}
                      onChange={(e) => setBrandSettings({...brandSettings, name: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Industry</label>
                    <input 
                      type="text" 
                      className="input w-full" 
                      placeholder="e.g. SaaS, Healthcare"
                      value={brandSettings.industry || ''}
                      onChange={(e) => setBrandSettings({...brandSettings, industry: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Website</label>
                    <input 
                      type="url" 
                      className="input w-full" 
                      placeholder="https://yourcompany.com"
                      value={brandSettings.website || ''}
                      onChange={(e) => setBrandSettings({...brandSettings, website: e.target.value})}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Company Size</label>
                    <select 
                      className="input w-full"
                      value={brandSettings.size || ''}
                      onChange={(e) => setBrandSettings({...brandSettings, size: e.target.value})}
                    >
                      <option value="">Select size</option>
                      <option value="1-10">1-10</option>
                      <option value="11-50">11-50</option>
                      <option value="51-200">51-200</option>
                      <option value="201-1000">201-1000</option>
                      <option value="1000+">1000+</option>
                    </select>
                  </div>
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                    <textarea 
                      className="input w-full" 
                      rows={2} 
                      placeholder="Describe your company..."
                      value={brandSettings.description || ''}
                      onChange={(e) => setBrandSettings({...brandSettings, description: e.target.value})}
                    />
                  </div>
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Logo URL</label>
                    <input 
                      type="url" 
                      className="input w-full" 
                      placeholder="https://yourcompany.com/logo.png"
                      value={brandSettings.logo || ''}
                      onChange={(e) => setBrandSettings({...brandSettings, logo: e.target.value})}
                    />
                  </div>
                </div>
              </div>
              {/* Brand Colors */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Brand Colors</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Primary Color</label>
                    <div className="flex items-center gap-2">
                      <input 
                        type="color" 
                        className="w-12 h-12 p-0 border-none bg-transparent rounded cursor-pointer" 
                        value={brandSettings.primaryColor}
                        onChange={(e) => setBrandSettings({...brandSettings, primaryColor: e.target.value})}
                      />
                      <input 
                        type="text" 
                        className="input flex-1" 
                        value={brandSettings.primaryColor}
                        onChange={(e) => setBrandSettings({...brandSettings, primaryColor: e.target.value})}
                      />
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Secondary Color</label>
                    <div className="flex items-center gap-2">
                      <input 
                        type="color" 
                        className="w-12 h-12 p-0 border-none bg-transparent rounded cursor-pointer" 
                        value={brandSettings.secondaryColor}
                        onChange={(e) => setBrandSettings({...brandSettings, secondaryColor: e.target.value})}
                      />
                      <input 
                        type="text" 
                        className="input flex-1" 
                        value={brandSettings.secondaryColor}
                        onChange={(e) => setBrandSettings({...brandSettings, secondaryColor: e.target.value})}
                      />
                    </div>
                  </div>
                </div>
              </div>
              {/* Brand Voice & Tone */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Brand Voice & Tone</h3>
                <select 
                  className="input w-full"
                  value={brandSettings.brandVoice}
                  onChange={(e) => setBrandSettings({...brandSettings, brandVoice: e.target.value})}
                >
                  <option value="Professional and friendly">Professional and friendly</option>
                  <option value="Professional and formal">Professional and formal</option>
                  <option value="Friendly and conversational">Friendly and conversational</option>
                  <option value="Confident and authoritative">Confident and authoritative</option>
                  <option value="Innovative and cutting-edge">Innovative and cutting-edge</option>
                </select>
              </div>
              {/* Font Family */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Font Family</h3>
                <select 
                  className="input w-full"
                  value={brandSettings.fontFamily}
                  onChange={(e) => setBrandSettings({...brandSettings, fontFamily: e.target.value})}
                >
                  <option value="Inter">Inter (Modern)</option>
                  <option value="Roboto">Roboto (Clean)</option>
                  <option value="Open Sans">Open Sans (Friendly)</option>
                  <option value="Lato">Lato (Professional)</option>
                  <option value="Poppins">Poppins (Contemporary)</option>
                </select>
              </div>
              {/* Value Propositions */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Value Propositions</h3>
                <div className="space-y-3">
                  {brandSettings.valueProps?.map((prop: string, index: number) => (
                    <div key={index} className="flex items-center gap-2">
                      <input 
                        type="text" 
                        className="input flex-1" 
                        placeholder="Enter value proposition..."
                        value={prop}
                        onChange={(e) => {
                          const newValueProps = [...(brandSettings.valueProps || [])];
                          newValueProps[index] = e.target.value;
                          setBrandSettings({...brandSettings, valueProps: newValueProps});
                        }}
                      />
                      <button 
                        type="button"
                        className="text-red-500 hover:text-red-700 p-2"
                        onClick={() => {
                          const newValueProps = brandSettings.valueProps?.filter((_, i) => i !== index) || [];
                          setBrandSettings({...brandSettings, valueProps: newValueProps});
                        }}
                      >
                        ✕
                      </button>
                    </div>
                  ))}
                  <button 
                    type="button"
                    className="text-primary-600 hover:text-primary-700 text-sm font-medium flex items-center gap-1"
                    onClick={() => {
                      const newValueProps = [...(brandSettings.valueProps || []), ''];
                      setBrandSettings({...brandSettings, valueProps: newValueProps});
                    }}
                  >
                    + Add Value Proposition
                  </button>
                </div>
              </div>
              {/* Brand Guidelines */}
              <div>
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Brand Guidelines</h3>
                <textarea 
                  className="input w-full" 
                  rows={3} 
                  placeholder="Additional brand guidelines, tone preferences, or specific requirements..."
                  value={brandSettings.brandGuidelines || ''}
                  onChange={(e) => setBrandSettings({...brandSettings, brandGuidelines: e.target.value})}
                />
              </div>
              <div className="flex justify-end gap-3 pt-4">
                <button 
                  type="button" 
                  className="bg-gray-200 hover:bg-gray-300 text-gray-800 py-2 px-6 rounded-lg font-medium" 
                  onClick={() => setShowBrandModal(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="bg-primary-600 hover:bg-primary-700 text-white py-2 px-6 rounded-lg font-medium disabled:opacity-50"
                  disabled={savingBrand}
                >
                  {savingBrand ? 'Saving...' : 'Save Changes'}
                </button>
              </div>
            </form>
            <button className="absolute top-4 right-4 text-gray-400 hover:text-gray-700 text-2xl" onClick={() => setShowBrandModal(false)} title="Close">&times;</button>
          </div>
        </div>
      )}
    </div>
  );
}
