import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { proposalsAPI } from '../services/api';
import ClientSelectionModal from '../components/ClientSelectionModal';

const sectionTitle = (title: string) => (
  <h2 className="text-lg font-semibold text-gray-800 mb-2 mt-6 flex items-center gap-2">
    <span className="w-1.5 h-5 bg-blue-500 rounded-sm mr-2"></span>{title}
  </h2>
);

const ProposalEditor: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [generating, setGenerating] = useState(false);
  const [showClientModal, setShowClientModal] = useState(false);
  const [selectedClientId, setSelectedClientId] = useState<string | undefined>();
  const [proposal, setProposal] = useState<any>({
    title: '',
    description: '',
    clientName: '',
    clientEmail: '',
    type: 'PROPOSAL',
    content: {
      executiveSummary: '',
      approach: '',
      budgetDetails: '',
      timeline: '',
      budget: ''
    },
    metadata: {
      industry: '',
      companySize: '',
      projectScope: ''
    }
  });

  const isNewProposal = !id;

  useEffect(() => {
    if (!isNewProposal && id) {
      const fetchProposal = async () => {
        try {
          setLoading(true);
          const response = await proposalsAPI.getById(id);
          const data = response.data.data;
          setProposal({
            ...data,
            content: data.content || {
              executiveSummary: '',
              approach: '',
              budgetDetails: '',
              timeline: '',
              budget: ''
            },
            metadata: data.metadata || {
              industry: '',
              companySize: '',
              projectScope: ''
            }
          });
        } catch (error) {
          console.error('Failed to fetch proposal:', error);
          toast.error('Failed to load proposal');
        } finally {
          setLoading(false);
        }
      };
      fetchProposal();
    }
    // If creating a new proposal and caseStudy is passed in location.state, prefill fields
    if (!id && location.state && location.state.caseStudy) {
      const caseStudy = location.state.caseStudy;
      setProposal((prev: any) => ({
        ...prev,
        title: caseStudy.title || '',
        description: caseStudy.description || '',
        clientName: caseStudy.clientName || '',
        metadata: {
          ...prev.metadata,
          industry: caseStudy.industry || '',
        },
        content: {
          ...prev.content,
          executiveSummary: caseStudy.challenge || '',
          approach: caseStudy.solution || '',
          budgetDetails: caseStudy.results || '',
        }
      }));
    }
    // If creating a new proposal and snippet is passed in location.state, prefill executive summary or approach
    if (!id && location.state && location.state.snippet) {
      const snippet = location.state.snippet;
      setProposal((prev: any) => ({
        ...prev,
        title: prev.title || snippet.title || '',
        content: {
          ...prev.content,
          executiveSummary: prev.content.executiveSummary || snippet.content || '',
        }
      }));
    }
    // If creating a new proposal and template is passed in location.state, prefill with template content
    if (!id && location.state && location.state.template) {
      const template = location.state.template;
      setProposal((prev: any) => ({
        ...prev,
        title: prev.title || template.name || '',
        description: prev.description || template.description || '',
        content: {
          ...prev.content,
          executiveSummary: prev.content.executiveSummary || template.content?.executiveSummary || '',
          approach: prev.content.approach || template.content?.approach || '',
          budgetDetails: prev.content.budgetDetails || template.content?.budgetDetails || '',
          timeline: prev.content.timeline || template.content?.timeline || '',
          budget: prev.content.budget || template.content?.budget || '',
        },
        metadata: {
          ...prev.metadata,
          industry: prev.metadata?.industry || template.content?.industry || '',
          category: template.category || '',
        }
      }));
    }
  }, [id, isNewProposal]);

  const handleGenerateWithAI = async () => {
    // Validate required fields
    if (!proposal.clientName || !proposal.description) {
      toast.error('Please fill in Client Name and Description before generating with AI');
      return;
    }

    try {
      setGenerating(true);

      const aiRequest = {
        clientName: proposal.clientName,
        clientEmail: proposal.clientEmail,
        projectDescription: proposal.description,
        budget: proposal.content?.budget || '',
        timeline: proposal.content?.timeline || '',
        requirements: [],
        customInstructions: `Industry: ${proposal.metadata?.industry || ''}, Company Size: ${proposal.metadata?.companySize || ''}, Project Scope: ${proposal.metadata?.projectScope || ''}`
      };

      const response = await proposalsAPI.generate(aiRequest);

      if (response.data.success) {
        const generatedProposal = response.data.data.proposal;

        // Update the form with generated content
        setProposal({
          ...proposal,
          title: generatedProposal.title,
          content: {
            executiveSummary: generatedProposal.content?.executiveSummary || '',
            approach: generatedProposal.content?.approach || '',
            budgetDetails: generatedProposal.content?.budgetDetails || '',
            timeline: generatedProposal.content?.timeline || proposal.content.timeline,
            budget: generatedProposal.content?.budget || proposal.content.budget
          }
        });

        toast.success('Proposal generated successfully with AI!');

        // If this was a new proposal, navigate to the generated proposal
        if (isNewProposal) {
          navigate(`/proposals/${generatedProposal.id}`);
        }
      } else {
        toast.error('Failed to generate proposal with AI');
      }
    } catch (error: any) {
      console.error('AI generation error:', error);
      toast.error(error.response?.data?.error || 'Failed to generate proposal with AI');
    } finally {
      setGenerating(false);
    }
  };

  const handleSave = async () => {
    try {
      setLoading(true);

      if (isNewProposal) {
        // Create new proposal
        const response = await proposalsAPI.create(proposal);
        toast.success('Proposal created successfully!');
        navigate(`/proposals/${response.data.data.id}`);
      } else {
        // Update existing proposal
        await proposalsAPI.update(id!, proposal);
        toast.success('Proposal updated successfully!');
      }

      navigate('/proposals');
    } catch (error: any) {
      console.error('Save error:', error);
      toast.error(error.response?.data?.error || 'Failed to save proposal');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);

      if (isNewProposal) {
        // Create and submit new proposal
        const response = await proposalsAPI.create({
          ...proposal,
          status: 'IN_REVIEW'
        });
        toast.success('Proposal submitted successfully!');
        navigate('/proposals');
      } else {
        // Update and submit existing proposal
        await proposalsAPI.update(id!, {
          ...proposal,
          status: 'IN_REVIEW'
        });
        toast.success('Proposal submitted successfully!');
        navigate('/proposals');
      }
    } catch (error: any) {
      console.error('Submit error:', error);
      toast.error(error.response?.data?.error || 'Failed to submit proposal');
    } finally {
      setLoading(false);
    }
  };

  const handleClientSelect = (client: any) => {
    setProposal({
      ...proposal,
      clientName: client.name,
      clientEmail: client.email || '',
    });
    setSelectedClientId(client.id);
    toast.success(`Selected client: ${client.name}`);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-20 w-20 border-b-4 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-10 px-2">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-xl p-10 border border-gray-100">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-8 gap-4">
          <h1 className="text-3xl sm:text-4xl font-extrabold text-gray-900 tracking-tight">
            {isNewProposal ? 'Create New Proposal' : 'Edit Proposal'}
          </h1>
          <div className="flex flex-row gap-3 justify-end">
            <button
              onClick={handleGenerateWithAI}
              disabled={generating || loading}
              className="inline-flex items-center px-5 py-2.5 bg-green-600 text-white font-semibold rounded-lg shadow-sm hover:bg-green-700 focus:ring-2 focus:ring-green-400 focus:outline-none transition disabled:opacity-50 gap-2"
            >
              {generating ? (
                <>
                  <svg className="animate-spin h-5 w-5 mr-2 text-white" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" /><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z" /></svg>
                  Generating...
                </>
              ) : (
                <>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" /></svg>
                  Generate with AI
                </>
              )}
            </button>
            <button
              onClick={handleSave}
              disabled={loading}
              className="inline-flex items-center px-5 py-2.5 bg-gray-700 text-white font-semibold rounded-lg shadow-sm hover:bg-gray-800 focus:ring-2 focus:ring-gray-400 focus:outline-none transition disabled:opacity-50 gap-2"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" /></svg>
              Save Draft
            </button>
            <button
              onClick={handleSubmit}
              disabled={loading}
              className="inline-flex items-center px-5 py-2.5 bg-blue-600 text-white font-semibold rounded-lg shadow-sm hover:bg-blue-700 focus:ring-2 focus:ring-blue-400 focus:outline-none transition disabled:opacity-50 gap-2"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" /></svg>
              Submit Proposal
            </button>
          </div>
        </div>
        <form className="space-y-8">
          {sectionTitle('Basic Information')}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Proposal Title</label>
              <input
                type="text"
                value={proposal.title}
                onChange={(e) => setProposal({ ...proposal, title: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter proposal title"
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Client Name</label>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={proposal.clientName}
                  onChange={(e) => setProposal({ ...proposal, clientName: e.target.value })}
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                  placeholder="Enter client name"
                />
                <button
                  type="button"
                  onClick={() => setShowClientModal(true)}
                  className={`px-4 py-2 rounded-lg transition-colors flex items-center gap-2 whitespace-nowrap ${
                    selectedClientId 
                      ? 'bg-green-600 text-white hover:bg-green-700' 
                      : 'bg-blue-600 text-white hover:bg-blue-700'
                  }`}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                  </svg>
                  {selectedClientId ? 'Change' : 'Select'}
                </button>
              </div>
              {selectedClientId && (
                <div className="mt-2 p-2 bg-green-50 border border-green-200 rounded-lg">
                  <div className="flex items-center gap-2 text-sm text-green-800">
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                    </svg>
                    Client selected from database
                  </div>
                </div>
              )}
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Client Email</label>
              <input
                type="email"
                value={proposal.clientEmail}
                onChange={(e) => setProposal({ ...proposal, clientEmail: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter client email"
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Description</label>
              <textarea
                value={proposal.description}
                onChange={(e) => setProposal({ ...proposal, description: e.target.value })}
                rows={2}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter proposal description"
              />
            </div>
          </div>
          <hr className="my-6 border-gray-200" />
          {sectionTitle('Project Details')}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Budget</label>
              <input
                type="text"
                value={proposal.content.budget}
                onChange={(e) => setProposal({ ...proposal, content: { ...proposal.content, budget: e.target.value } })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter budget"
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Timeline</label>
              <input
                type="text"
                value={proposal.content.timeline}
                onChange={(e) => setProposal({ ...proposal, content: { ...proposal.content, timeline: e.target.value } })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter timeline"
              />
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Industry</label>
              <input
                type="text"
                value={proposal.metadata.industry}
                onChange={(e) => setProposal({ ...proposal, metadata: { ...proposal.metadata, industry: e.target.value } })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="e.g., Technology"
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Company Size</label>
              <input
                type="text"
                value={proposal.metadata.companySize}
                onChange={(e) => setProposal({ ...proposal, metadata: { ...proposal.metadata, companySize: e.target.value } })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="e.g., 51-200"
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Project Scope</label>
              <input
                type="text"
                value={proposal.metadata.projectScope}
                onChange={(e) => setProposal({ ...proposal, metadata: { ...proposal.metadata, projectScope: e.target.value } })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="e.g., Full development"
              />
            </div>
          </div>
          <hr className="my-6 border-gray-200" />
          {sectionTitle('Proposal Content')}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Executive Summary</label>
              <textarea
                value={proposal.content.executiveSummary}
                onChange={(e) => setProposal({ ...proposal, content: { ...proposal.content, executiveSummary: e.target.value } })}
                rows={3}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter executive summary..."
              />
            </div>
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Approach</label>
              <textarea
                value={proposal.content.approach}
                onChange={(e) => setProposal({ ...proposal, content: { ...proposal.content, approach: e.target.value } })}
                rows={3}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter approach..."
              />
            </div>
          </div>
          <div className="grid grid-cols-1 gap-6">
            <div>
              <label className="block text-base font-medium text-gray-700 mb-1">Budget Details</label>
              <textarea
                value={proposal.content.budgetDetails}
                onChange={(e) => setProposal({ ...proposal, content: { ...proposal.content, budgetDetails: e.target.value } })}
                rows={4}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-900 text-base"
                placeholder="Enter detailed budget breakdown, pricing rationale, and cost structure..."
              />
            </div>
          </div>
        </form>
      </div>

      {/* Client Selection Modal */}
      <ClientSelectionModal
        isOpen={showClientModal}
        onClose={() => setShowClientModal(false)}
        onSelectClient={handleClientSelect}
        selectedClientId={selectedClientId}
      />
    </div>
  );
};

export default ProposalEditor;
