import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { proposalsAPI } from '../services/api';

interface Proposal {
  id: string;
  title: string;
  description?: string;
  clientName: string;
  clientEmail?: string;
  status: string;
  type: string;
  content: any;
  metadata?: any;
  version: number;
  isPublic: boolean;
  publicUrl?: string;
  createdAt: string;
  updatedAt: string;
  publishedAt?: string;
  user: {
    firstName: string;
    lastName: string;
    email: string;
  };
  _count: {
    comments: number;
    activities: number;
  };
}

interface ClientProposalsProps {
  clientName: string;
  isExpanded: boolean;
}

const ClientProposals: React.FC<ClientProposalsProps> = ({ clientName, isExpanded }) => {
  const navigate = useNavigate();
  const [proposals, setProposals] = useState<Proposal[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (isExpanded && clientName) {
      fetchProposals();
    }
  }, [isExpanded, clientName]);

  const fetchProposals = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await proposalsAPI.getByClient(clientName);
      if (response.data.success) {
        setProposals(response.data.data);
      } else {
        setError('Failed to fetch proposals');
      }
    } catch (error) {
      console.error('Fetch proposals error:', error);
      setError('Failed to fetch proposals');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'WON': return 'bg-green-100 text-green-800 border-green-200';
      case 'LOST': return 'bg-red-100 text-red-800 border-red-200';
      case 'IN_REVIEW': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'SENT': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'DRAFT': return 'bg-gray-100 text-gray-800 border-gray-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'WON': return 'üèÜ';
      case 'LOST': return '‚ùå';
      case 'IN_REVIEW': return '‚è≥';
      case 'SENT': return 'üì§';
      case 'DRAFT': return 'üìù';
      default: return 'üìÑ';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const getBudgetDisplay = (content: any) => {
    if (content?.budgetDetails?.total) {
      return `$${content.budgetDetails.total.toLocaleString()}`;
    }
    if (content?.budget?.total) {
      return `$${content.budget.total.toLocaleString()}`;
    }
    if (content?.pricing?.total) {
      return `$${content.pricing.total.toLocaleString()}`;
    }
    return 'N/A';
  };

  if (!isExpanded) return null;

  if (loading) {
    return (
      <div className="bg-gray-50 border-t border-gray-200 p-6">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <span className="ml-3 text-gray-600">Loading proposals...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-gray-50 border-t border-gray-200 p-6">
        <div className="text-center py-8">
          <div className="text-red-500 mb-2">‚ö†Ô∏è</div>
          <p className="text-red-600">{error}</p>
        </div>
      </div>
    );
  }

  if (proposals.length === 0) {
