import React, { useState, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import ReactMarkdown from 'react-markdown';
import {
  DocumentTextIcon,
  DocumentDuplicateIcon,
  UsersIcon,
  ChartBarIcon,
  ArrowUpIcon,
  ArrowDownIcon,
  EyeIcon,
  PlusIcon,
  PaperAirplaneIcon,
  EnvelopeIcon
} from '@heroicons/react/24/outline';
import { generateProposalSuggestions } from '../services/aiService';
import { sendProposalEmail } from '../services/emailService';

interface Suggestion {
  id: string;
  title: string;
  content: string;
  category: string;
}

export default function Dashboard() {
  const [showContent, setShowContent] = useState(false);
  const [generatedContent, setGeneratedContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [suggestions, setSuggestions] = useState<Suggestion[]>([]);
  const [selectedSuggestions, setSelectedSuggestions] = useState<string[]>([]);
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [emailData, setEmailData] = useState({
    to: '',
    subject: '',
    message: '',
    clientName: '',
    clientEmail: ''
  });

  // Mock data for Larkon-style dashboard
  const stats = [
    {
      title: 'Total Proposals',
      value: '1,234',
      change: { value: 12, isPositive: true },
      icon: DocumentTextIcon,
      iconBg: 'blue',
      link: 'View all proposals'
    },
    {
      title: 'Draft Proposals',
      value: '45',
      change: { value: 8, isPositive: true },
      icon: DocumentDuplicateIcon,
      iconBg: 'green',
      link: 'View drafts'
    },
    {
      title: 'Sent Proposals',
      value: '892',
      change: { value: 3, isPositive: false },
      icon: DocumentDuplicateIcon,
      iconBg: 'orange',
      link: 'View sent'
    },
    {
      title: 'Active Clients',
      value: '156',
      change: { value: 15, isPositive: true },
      icon: UsersIcon,
      iconBg: 'purple',
      link: 'View clients'
    }
  ];

  const recentActivity = [
    {
      id: 1,
      text: 'New proposal "Website Redesign" created',
      time: '2 hours ago',
      icon: DocumentTextIcon
    },
    {
      id: 2,
      text: 'Proposal "Marketing Strategy" sent to client',
      time: '4 hours ago',
      icon: DocumentDuplicateIcon
    },
    {
      id: 3,
      text: 'Client "TechCorp Inc" added to system',
      time: '6 hours ago',
      icon: UsersIcon
    },
    {
      id: 4,
      text: 'Proposal "Mobile App Development" approved',
      time: '1 day ago',
      icon: ChartBarIcon
    }
  ];

  // Load suggestions on component mount
  useEffect(() => {
    loadSuggestions();
  }, []);

  const loadSuggestions = async () => {
    try {
      const newSuggestions = await generateProposalSuggestions();
      setSuggestions(newSuggestions);
    } catch (error) {
      console.error('Error loading suggestions:', error);
      toast.error('Failed to load suggestions');
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleGenerateWithAI = async () => {
    if (!selectedFile) {
      toast.error('Please select a PDF file first');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('pdf', selectedFile);

      const response = await fetch('/api/proposals/extract-pdf', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to process PDF');
      }

      const data = await response.json();
      setGeneratedContent(data.content);
      setShowContent(true);
      toast.success('Content generated successfully!');
    } catch (error) {
      console.error('Error generating content:', error);
      toast.error('Failed to generate content. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setSelectedFile(null);
    setGeneratedContent('');
    setShowContent(false);
    setSelectedSuggestions([]);
    const fileInput = document.getElementById('pdf-upload') as HTMLInputElement;
    if (fileInput) {
      fileInput.value = '';
    }
  };

  const handleSuggestionClick = (suggestion: Suggestion) => {
    setSelectedSuggestions(prev => {
      if (prev.includes(suggestion.id)) {
        return prev.filter(id => id !== suggestion.id);
      } else {
        return [...prev, suggestion.id];
      }
    });
  };

  const handleSendEmail = async () => {
    if (!emailData.to || !emailData.subject || !emailData.message) {
      toast.error('Please fill in all required fields');
      return;
    }

    try {
      await sendProposalEmail({
        to: emailData.to,
        subject: emailData.subject,
        message: emailData.message,
        clientName: emailData.clientName,
        clientEmail: emailData.clientEmail
      });

      toast.success('Email sent successfully!');
      setShowEmailModal(false);
      setEmailData({ to: '', subject: '', message: '', clientName: '', clientEmail: '' });
    } catch (error) {
      console.error('Error sending email:', error);
      toast.error('Failed to send email');
    }
  };

  const selectedSuggestionsContent = suggestions
    .filter(s => selectedSuggestions.includes(s.id))
    .map(s => s.content)
    .join('\n\n');

  return (
    <div>
      {/* Welcome Section */}
      <div className="dashboard-welcome">
        <h1 className="welcome-title">WELCOME!</h1>
        <p className="welcome-subtitle">
          Here's what's happening with your proposals today.
        </p>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="stat-card">
              <div className="stat-header">
                <div className="stat-title">{stat.title}</div>
                <div className={`stat-icon ${stat.iconBg}`}>
                  <Icon className="h-5 w-5" />
                </div>
              </div>
              <div className="stat-value">{stat.value}</div>
              <div className={`stat-change ${stat.change.isPositive ? 'positive' : 'negative'}`}>
                {stat.change.isPositive ? (
                  <ArrowUpIcon className="h-4 w-4" />
                ) : (
                  <ArrowDownIcon className="h-4 w-4" />
                )}
                {stat.change.value}%
              </div>
              <div className="stat-footer">
                <button className="stat-link">
                  {stat.link}
                  <EyeIcon className="h-4 w-4" />
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {/* Chart Section */}
      <div className="chart-section">
        <div className="chart-header">
          <h2 className="chart-title">Performance Overview</h2>
          <button className="btn btn-secondary btn-sm">
            View Details
          </button>
        </div>
        <div className="chart-placeholder">
          Chart visualization will be displayed here
        </div>
      </div>

      {/* Quick Proposal Generator */}
      <div className="bg-white rounded-lg p-6 border border-gray-200 shadow-sm mb-6">
        <h2 className="text-lg font-semibold text-primary mb-4">Quick Proposal Generator</h2>
        <div className="space-y-4">
          <div>
            <label htmlFor="pdf-upload" className="block text-sm font-medium text-secondary mb-2">
              Upload PDF Proposal
            </label>
            <input
              id="pdf-upload"
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              className="input"
            />
          </div>
          <div className="flex gap-3">
            <button
              onClick={handleGenerateWithAI}
              disabled={!selectedFile || loading}
              className="btn btn-primary"
            >
              {loading ? 'Generating...' : 'Generate Proposal'}
            </button>
            <button
              onClick={handleClear}
              className="btn btn-secondary"
            >
              Clear
            </button>
          </div>
        </div>
      </div>

      {/* AI Suggestions */}
      <div className="bg-white rounded-lg p-6 border border-gray-200 shadow-sm mb-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-primary">AI Proposal Suggestions</h2>
          <button
            onClick={loadSuggestions}
            className="btn btn-secondary btn-sm"
          >
            <PlusIcon className="h-4 w-4" />
            Refresh
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-4">
          {suggestions.map((suggestion) => (
            <div
              key={suggestion.id}
              className={`p-4 rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                selectedSuggestions.includes(suggestion.id)
                  ? 'border-orange-500 bg-orange-50'
                  : 'border-gray-200 hover:border-gray-300'
              }`}
              onClick={() => handleSuggestionClick(suggestion)}
            >
              <h3 className="font-medium text-primary mb-2">{suggestion.title}</h3>
              <p className="text-sm text-secondary line-clamp-3">{suggestion.content}</p>
              <span className="inline-block mt-2 px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded">
                {suggestion.category}
              </span>
            </div>
          ))}
        </div>

        {selectedSuggestions.length > 0 && (
          <div className="mt-4 p-4 bg-gray-50 rounded-lg">
            <h3 className="font-medium text-primary mb-2">Selected Suggestions ({selectedSuggestions.length})</h3>
            <p className="text-sm text-secondary">
              These suggestions will be included in your AI generation.
            </p>
          </div>
        )}
      </div>

      {/* Recent Activity */}
      <div className="activity-section">
        <div className="activity-header">
          <h2 className="activity-title">Recent Activity</h2>
          <button className="btn btn-secondary btn-sm">
            View All
          </button>
        </div>
        <div className="space-y-3">
          {recentActivity.map((activity) => {
            const Icon = activity.icon;
            return (
              <div key={activity.id} className="activity-item">
                <div className="activity-icon">
                  <Icon className="h-5 w-5" />
                </div>
                <div className="activity-content">
                  <div className="activity-text">{activity.text}</div>
                  <div className="activity-time">{activity.time}</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Generated Content Display */}
      {showContent && (
        <div className="bg-white rounded-lg p-6 border border-gray-200 shadow-sm mt-6">
          <h2 className="text-lg font-semibold text-primary mb-4">Generated Content</h2>
          <div className="prose prose-sm max-w-none text-secondary mb-4">
            <ReactMarkdown>{generatedContent}</ReactMarkdown>
          </div>
          <div className="flex gap-3">
            <button className="btn btn-primary">
              Save as Draft
            </button>
            <button 
              onClick={() => setShowEmailModal(true)}
              className="btn btn-secondary"
            >
              <EnvelopeIcon className="h-4 w-4" />
              Send Email
            </button>
          </div>
        </div>
      )}

      {/* Email Modal */}
      {showEmailModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-lg font-semibold text-primary mb-4">Send Proposal Email</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-secondary mb-1">
                  Client Name
                </label>
                <input
                  type="text"
                  value={emailData.clientName}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientName: e.target.value }))}
                  className="input"
                  placeholder="Enter client name"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-secondary mb-1">
                  Client Email *
                </label>
                <input
                  type="email"
                  value={emailData.clientEmail}
                  onChange={(e) => setEmailData(prev => ({ ...prev, clientEmail: e.target.value }))}
                  className="input"
                  placeholder="Enter client email"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-secondary mb-1">
                  Subject *
                </label>
                <input
                  type="text"
                  value={emailData.subject}
                  onChange={(e) => setEmailData(prev => ({ ...prev, subject: e.target.value }))}
                  className="input"
                  placeholder="Enter email subject"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-secondary mb-1">
                  Message *
                </label>
                <textarea
                  value={emailData.message}
                  onChange={(e) => setEmailData(prev => ({ ...prev, message: e.target.value }))}
                  className="input min-h-[100px]"
                  placeholder="Enter your message"
                  required
                />
              </div>
            </div>

            <div className="flex gap-3 mt-6">
              <button
                onClick={handleSendEmail}
                className="btn btn-primary flex-1"
              >
                <PaperAirplaneIcon className="h-4 w-4" />
                Send Email
              </button>
              <button
                onClick={() => setShowEmailModal(false)}
                className="btn btn-secondary"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Loading Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 flex items-center space-x-3">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-orange-500"></div>
            <span className="text-primary">Generating proposal...</span>
          </div>
        </div>
      )}
    </div>
  );
}
